package loops;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Use Case: Fixed number of iterations
		//Key Feature: Best when the count is known.
		
		for (int i = 0; i <=10; i++) {
			if (i%2==0) {
				System.out.println(i);
				
			}
			
		}

	}

}
